import React from 'react';
import ReactDOM from 'react-dom';

const Scroll = (props) => {

	return (
		<div style={{overflow: 'scroll', border: '1px solid black', height: '500px'}}>
			{props.children}
		</div>

		);
};

export default Scroll;
