import React from 'react';

const Card = (props) => {
  return (
        // this is JSX read by React, not HTML
        // can only return a single element, 
        // like a single div container or an array

        // initially it was
        // <h2>Jane Doe</h2>
        // <p>jane.doe@gmail.com</p>

        // tachyon classNames bg-light-green
        <div className='bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5'>
          <img src={`https://robohash.org/${props.id}`} style={{height: '200px', width: '200px'}} alt='alt photo'/>
          <div>
              <h2>{props.name}</h2>
              <p>{props.email}</p>
          </div>
        </div>
        );
};

export default Card;
